import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import LozinkaPage from "./pages/LozinkaPage";
import PromenaLozinke from "./pages/PromenaLozinke";
import MeniPage from "./pages/MeniPage";
import PregledPage from "./pages/PregledPage";
import AdminPregled from "./pages/AdminPregled";
import AdminKorisnici from "./pages/AdminKorisnici";
import AdminMeniji from "./pages/AdminMeniji";
import AdminObroci from "./pages/AdminObroci";
import AdminRole from "./pages/AdminRole";
import AdminLokacije from "./pages/AdminLokacije";
import AdminKompanije from "./pages/AdminKompanije";
import NotAuthorizedPage from "./pages/NotAuthorizedPage";
import NePostojiPage from "./pages/NePostojiPage";
import ProtectedRoute from "./components/ProtectedRoute";

const App = () => {
  const [token, setToken] = useState(localStorage.getItem("token") || "");

  // SAFE token decode — NEVER crashes
  const getUserFromToken = () => {
    const storedToken = localStorage.getItem("token");
    if (!storedToken) return null;

    try {
      // This handles malformed base64, padding, etc.
      const base64Url = storedToken.split('.')[1];
      if (!base64Url) return null;
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = atob(base64 + '=='.substring(0, (4 - base64.length % 4) % 4));
      return JSON.parse(jsonPayload);
    } catch (e) {
      console.warn("Invalid token in storage, clearing...");
      localStorage.removeItem("token");
      setToken("");
      return null;
    }
  };

  const user = getUserFromToken();

  // Update token globally
  const updateToken = (newToken) => {
    if (newToken) {
      localStorage.setItem("token", newToken);
    } else {
      localStorage.removeItem("token");
    }
    setToken(newToken || "");
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage updateToken={updateToken} />} />
        
        {/* Protected admin routes */}
        {user && (user.role === "ROLE_ADMIN" || user.role === "ADMIN") ? (
          <>
            <Route path="/admin" element={<AdminDashboard />} />
            <Route path="/admin/obroci" element={<AdminObroci />} />
            <Route path="/admin/lokacije" element={<AdminLokacije />} />
            <Route path="/admin/role" element={<AdminRole />} />
            <Route path="/admin/korisnici" element={<AdminKorisnici />} />
            <Route path="/admin/pregled" element={<AdminPregled />} />
            {/* Add all your other admin routes here */}
          </>
        ) : (
          <Route path="*" element={<LoginPage updateToken={updateToken} />} />
        )}

        {/* Public routes */}
        <Route path="/" element={<LoginPage updateToken={updateToken} />} />
        {/* Add other public pages */}
      </Routes>
    </Router>
  );
};

export default App;
